    private void buscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buscarActionPerformed
        String codigoTexto = filtro.getText().trim();
        try {
        int codigo = Integer.parseInt(codigoTexto);
        ProdutoControle controle = new ProdutoControle(); // ADICIONE ESTA LINHA
        Produto p = controle.buscarPorCodigo(codigo);

            if (p != null) {
                editarNome.setText(p.getNome());
                editarProc.setText(p.getProcedencia());
                editarQtdInicial.setText(String.valueOf(p.getQtdInicial()));
                editarQtdAtual.setText(String.valueOf(p.getQtdAtual()));
            } else {
                JOptionPane.showMessageDialog(this, "Produto não encontrado.");
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Código inválido.");
        }
    }//GEN-LAST:event_buscarActionPerformed

