    private void limparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_limparActionPerformed
        //limpa os campos
        filtro.setText("");
        editarNome.setText("");
        editarProc.setText("");
        editarQtdInicial.setText("");
        editarQtdAtual.setText("");
        filtro.requestFocus();
    }//GEN-LAST:event_limparActionPerformed

    private void salvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_salvarActionPerformed
        String codigoTexto = filtro.getText().trim();
        try {
            int codigo = Integer.parseInt(codigoTexto);
            ProdutoControle controle = new ProdutoControle();
            Produto produto = controle.buscarPorCodigo(codigo);
            if (produto != null) {
                produto.setNome(editarNome.getText().trim());
                produto.setProcedencia(editarProc.getText().trim());
                produto.setQtdInicial(Integer.parseInt(editarQtdInicial.getText().trim()));
                produto.setQtdAtual(Integer.parseInt(editarQtdAtual.getText().trim()));
                JOptionPane.showMessageDialog(null, "Produto atualizado com sucesso.");
            } else {
                JOptionPane.showMessageDialog(null, "Produto não encontrado para atualização.");
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Erro ao atualizar produto: verifique os campos.");
        }
    }//GEN-LAST:event_salvarActionPerformed

